// Shift-based hashing function to generate unique 32-bit integers from long URLs
function hashCode(str) {
    let hash = 0;
    for (let i = 0, len = str.length; i < len; i++) {
        let chr = str.charCodeAt(i);
        // this is equivalent to 31 * hash, but slightly more performant due to using a shift and subtraction rather than multiplication
        // why 31? it's odd and prime and easily converted into the more performant shift + subtraction method
        hash = (hash << 5) - hash + chr;
        // Convert to 32bit
        hash |= 0;
    }
    return hash;
}

// Hash codes can be negative and JavaScript's implementation of modulo doesn't work well for negative numbers
// see https://web.archive.org/web/20090717035140if_/javascript.about.com/od/problemsolving/a/modulobug.htm
function mod(n, m) {
    return ((n % m) + m) % m;
}
  
// Turn the hard-to-remember hash into an easy-to-remember emoji!
function emojiHash(str) {
    return emojis[mod(Math.floor(hashCode(str)),(emojis.length))];
}

// Helper method to check if a given URL is valid and of the expected format
function isValidURL(string) {
    let inputURL;

    try {
        inputURL = new URL(string);
    } catch (error) {
        console.log("Incorrect URL format provided. See following error: ", error)
        return false;
    }

    return inputURL.protocol === "http:" || inputURL.protocol === "https:";
}

/* Example request
curl --request POST \
  --url http://localhost:3000/shorten \
  --header 'Content-Type: application/json' \
  --data '{"url": "https://facebook.com"}'
*/

app.post('/shorten', (req, res) => {
    const origURL = req.body.url;
    const emoji = emojiHash(origURL);
    // need to check the URL here for validity and hash collisions. since the emoji is the key we can do this lookup in nearly-O(1) time
    if (isValidURL(origURL) && !urlTable.has(emoji)) {
        // insert unique emoji (key) with original URL (value) in map
        urlTable.set(emoji, origURL);
        res.send(JSON.stringify({
            shortURL: baseURL.concat(port, "/", emoji),
            longURL: origURL
        }));
    } else {
        res.send('Invalid or duplicate URL: ', origURL, ' - Make sure to include the http(s)://');
    }
});

/* Example request
curl --request GET \
  --url http://localhost:3000/💎 \
  --header 'Content-Type: application/json'
*/

// Route to redirect shortURLs to their original URL
app.get('/:emoji', (req, res) => {
    const redirectURL = urlTable.get(req.params.emoji);
    // if the redirect URL is falsey we should return a 404. otherwise redirect the client
    redirectURL ? res.redirect(redirectURL) : res.status(404).end();
});

app.listen(port, () => console.log(`url-emojifier listening on port ${port}!`))
